import java.util.HashSet;/** * Created by patimshi on 11/27/2016. */public class HelloJava {    public static void main(String shnux[]){        System.out.println("Hello java in INTELJ IDEA");        System.err.println("Hey! Police got shot, call an ambulance QUICK");        String planet = "mars";                HashSet<String> leaders = callTheWorldLeader(planet);        for (String leader :                leaders) {            System.out.println("Hello! Mr. "+leader);        }    }    public static HashSet<String> callTheWorldLeader(String planet) {        HashSet<String> leaders = new HashSet<>();        switch (planet){            case "Earth":                leaders.add("Modi");                leaders.add("Obama");                break;            default:                break;        }        return leaders;    }    }